create table author (id bigint not null, first_name varchar(255), last_name varchar(255), primary key (id)) engine=InnoDB;
create table author_book (book_id bigint not null, author_id bigint not null, primary key (book_id, author_id)) engine=InnoDB;
create table book (id bigint not null, isbn varchar(255), title varchar(255), publisher_id bigint, primary key (id)) engine=InnoDB;
create table category (id bigint not null auto_increment, description varchar(255), primary key (id)) engine=InnoDB;
